import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const bannerSlides = [
    {
        image: "https://p2-ofp.static.pub/ShareResource/ww/img/homepage-banners/lenovo-mwc-2025-hpb-teaser-desktop.jpg", 
        alt: "Banner 1",
        title: "Promo√ß√£o Especial",
        description: "Aproveite descontos exclusivos em toda a loja!"
    },
    {
        image: "https://p3-ofp.static.pub/ShareResource/ww/img/homepage-banners/lenovo-fifa-2024-hpb-desktop.jpg",
        alt: "Banner 2",
        title: "Novos Produtos",
        description: "Confira os lan√ßamentos Lenovo para 2025."
    },
    {
        image: "https://p2-ofp.static.pub/ShareResource/na/ftv/hero/lenovo-new-dawn.jpg",
        alt: "Banner 3",
        title: "Suporte Premium",
        description: "Tenha atendimento personalizado para seu neg√≥cio."

    }
];

const categories = [
    { icon: 'üí∏', label: 'Promo√ß√µes' },
    { icon: 'üíª', label: 'PCs & Tablets' },
    { icon: 'üì±', label: 'Smartphones' },
    { icon: 'üóÑÔ∏è', label: 'Servidores e Armazenamento' },
    { icon: 'üîß', label: 'Servi√ßos' },
    { icon: 'üéß', label: 'Suporte' },
    { icon: 'üè∑Ô∏è', label: 'Outlet' },
];

const profiles = [
    {
        title: 'Lenovo Pro para Neg√≥cios',
        description: 'Inscreva-se gratuitamente, obtenha benef√≠cios exclusivos e atendimento personalizado.',
        button: 'Saiba Mais',
        image: 'https://via.placeholder.com/300x180?text=Neg√≥cios'
    },
    {
        title: 'Gaming',
        description: 'Jogue de forma mais inteligente com a Lenovo.',
        button: 'Compre Agora',
        image: 'https://via.placeholder.com/300x180?text=Gaming'
    },
    {
        title: 'Educa√ß√£o',
        description: 'O futuro precisa de voc√™, libere sua paix√£o e construa um mundo melhor.',
        button: 'Compre Agora',
        image: 'https://via.placeholder.com/300x180?text=Educa√ß√£o'
    }
];

const Home = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const nextSlide = () => setCurrentSlide((currentSlide + 1) % bannerSlides.length);
    const prevSlide = () => setCurrentSlide((currentSlide - 1 + bannerSlides.length) % bannerSlides.length);

    // Troca autom√°tica do banner a cada 4 segundos
    useEffect(() => {
        const interval = setInterval(nextSlide, 4000);
        return () => clearInterval(interval);
    }, [currentSlide]);

    return (
        <div>
            <Header />
            <main>
                {/* Banner em slide */}
                <section style={{ position: 'relative', background: '#222', color: '#fff', padding: '0', textAlign: 'center' }}>
                    <div className="swiper-slide hero swiper-slide-active">
                        <img
                            src={bannerSlides[currentSlide].image}
                            alt={bannerSlides[currentSlide].title}
                            style={{ width: '100%', maxHeight: '700px', objectFit: 'cover', borderRadius: '0 0 13px 13px' }}
                        />
                        <div className="banner-content" style={{ position: 'absolute', top: '30px', left: '50%', transform: 'translateX(-50%)', background: 'rgba(0,0,0,0.5)', padding: '16px 32px', borderRadius: '12px' }}>
                            <h2>{bannerSlides[currentSlide].title}</h2>
                            <p>{bannerSlides[currentSlide].description}</p>
                            <a href="#" className="btn" style={{ background: '#e60012', color: '#fff', padding: '10px 20px', borderRadius: '6px', textDecoration: 'none', fontWeight: 'bold' }}>Saiba mais</a>
                        </div>
                    </div>
                    {/* Navega√ß√£o dos banners */}
                    <div style={{ position: 'absolute', bottom: '16px', left: '50%', transform: 'translateX(-50%)', display: 'flex', gap: '8px' }}>
                        {bannerSlides.map((_, idx) => (
                            <button
                                key={idx}
                                onClick={() => setCurrentSlide(idx)}
                                style={{
                                    width: '12px',
                                    height: '12px',
                                    borderRadius: '50%',
                                    border: 'none',
                                    background: idx === currentSlide ? '#e60012' : '#fff',
                                    opacity: idx === currentSlide ? 1 : 0.5,
                                    cursor: 'pointer',
                                    transition: 'background 0.2s'
                                }}
                                aria-label={`Ir para o banner ${idx + 1}`}
                            />
                        ))}
                    </div>
                </section>

                {/* Categorias */}
                <section style={{ background: '#fff', padding: '32px 0' }}>
                    <h3 style={{ textAlign: 'center' }}>Explore a Lenovo</h3>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '32px', marginTop: '24px' }}>
                        {categories.map(cat => (
                            <div key={cat.label} style={{ textAlign: 'center' }}>
                                <div style={{ fontSize: '2rem' }}>{cat.icon}</div>
                                <div>{cat.label}</div>
                            </div>
                        ))}
                    </div>
                </section>

                {/* Perfis de compra */}
                <section style={{ background: '#f9f9f9', padding: '32px 0' }}>
                    <h3 style={{ textAlign: 'center' }}>Compre de acordo com seu perfil</h3>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '32px', marginTop: '24px' }}>
                        {profiles.map(profile => (
                            <div key={profile.title} style={{ background: '#fff', borderRadius: '12px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', width: '300px', padding: '16px', textAlign: 'left' }}>
                                <img src={profile.image} alt={profile.title} style={{ width: '100%', borderRadius: '8px' }} />
                                <h4>{profile.title}</h4>
                                <p>{profile.description}</p>
                                <button style={{ background: '#fff', color: '#e60012', border: '1px solid #e60012', padding: '8px 16px', borderRadius: '6px', fontWeight: 'bold', cursor: 'pointer' }}>
                                    {profile.button}
                                </button>
                            </div>
                        ))}
                    </div>
                </section>
            </main>
            <Footer />
        </div>
    );
};

export default Home;
