import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const categories = [
    { icon: 'ðŸ’¸', label: 'PromoÃ§Ãµes' },
    { icon: 'ðŸ’»', label: 'PCs & Tablets' },
    { icon: 'ðŸ“±', label: 'Smartphones' },
    { icon: 'ðŸ—„ï¸', label: 'Servidores e Armazenamento' },
    { icon: 'ðŸ”§', label: 'ServiÃ§os' },
    { icon: 'ðŸŽ§', label: 'Suporte' },
    { icon: 'ðŸ·ï¸', label: 'Outlet' },
];

const profiles = [
    {
        title: 'Lenovo Pro para NegÃ³cios',
        description: 'Inscreva-se gratuitamente, obtenha benefÃ­cios exclusivos e atendimento personalizado.',
        button: 'Saiba Mais',
        image: 'https://via.placeholder.com/300x180?text=NegÃ³cios'
    },
    {
        title: 'Gaming',
        description: 'Jogue de forma mais inteligente com a Lenovo.',
        button: 'Compre Agora',
        image: 'https://via.placeholder.com/300x180?text=Gaming'
    },
    {
        title: 'EducaÃ§Ã£o',
        description: 'O futuro precisa de vocÃª, libere sua paixÃ£o e construa um mundo melhor.',
        button: 'Compre Agora',
        image: 'https://via.placeholder.com/300x180?text=EducaÃ§Ã£o'
    }
];

const Home = () => {
    return (
        <div>
            <Header />
            <main>
                {/* Banner principal */}
                <section style={{ background: 'linear-gradient(90deg, #6a1b9a, #e60012)', color: '#fff', padding: '48px 0', textAlign: 'center' }}>
                    <h2>Lenovo C</h2>
                    <p>A Lenovo libera o poder da IA pessoal e empresarial para construir um futuro mais inteligente para todos.</p>
                    <button style={{ background: '#fff', color: '#e60012', border: 'none', padding: '10px 24px', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer' }}>
                        Veja Contagem regressiva
                    </button>
                </section>

                {/* Categorias */}
                <section style={{ background: '#fff', padding: '32px 0' }}>
                    <h3 style={{ textAlign: 'center' }}>Explore a Lenovo</h3>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '32px', marginTop: '24px' }}>
                        {categories.map(cat => (
                            <div key={cat.label} style={{ textAlign: 'center' }}>
                                <div style={{ fontSize: '2rem' }}>{cat.icon}</div>
                                <div>{cat.label}</div>
                            </div>
                        ))}
                    </div>
                </section>

                {/* Perfis de compra */}
                <section style={{ background: '#f9f9f9', padding: '32px 0' }}>
                    <h3 style={{ textAlign: 'center' }}>Compre de acordo com seu perfil</h3>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '32px', marginTop: '24px' }}>
                        {profiles.map(profile => (
                            <div key={profile.title} style={{ background: '#fff', borderRadius: '12px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', width: '300px', padding: '16px', textAlign: 'left' }}>
                                <img src={profile.image} alt={profile.title} style={{ width: '100%', borderRadius: '8px' }} />
                                <h4>{profile.title}</h4>
                                <p>{profile.description}</p>
                                <button style={{ background: '#fff', color: '#e60012', border: '1px solid #e60012', padding: '8px 16px', borderRadius: '6px', fontWeight: 'bold', cursor: 'pointer' }}>
                                    {profile.button}
                                </button>
                            </div>
                        ))}
                    </div>
                </section>
            </main>
            <Footer />
        </div>
    );
};

export default Home;
