import React from 'react';
import { useParams } from 'react-router-dom';
import ProductList from '../components/ProductList';

const ProductsCategory = () => {
    const { category } = useParams();
    
    // Dados de exemplo - substitua por chamada API ou contexto
    const categories = {
        notebooks: [
            {
                id: 1,
                name: 'Lenovo Yoga Slim 7i',
                description: 'Notebook ultrafino com Intel Core i7 e tela 2.8K OLED',
                price: 6999.99,
                image: 'https://p3-ofp.static.pub/ShareResource/na/subseries/hero/lenovo-laptop-yoga-slim-7-carbon-13-intel-subseries-hero.png'
            },
            // Adicione mais produtos
        ],
        desktops: [
            // Adicione produtos para desktop
        ]
    };

    const products = categories[category] || [];

    return (
        <div style={{ padding: '32px 0' }}>
            <h1 style={{ textAlign: 'center', marginBottom: '32px', textTransform: 'capitalize' }}>
                {category.replace('-', ' ')}
            </h1>
            {products.length > 0 ? (
                <ProductList products={products} />
            ) : (
                <p style={{ textAlign: 'center' }}>Nenhum produto encontrado nesta categoria.</p>
            )}
        </div>
    );
};

export default ProductsCategory;
